import React, { Component } from 'react';

class Counter extends Component {
    state = {
        counter: 0
    }

    handleClick(){
        this.setState({
            count: this.state.counter + 1
        })
    }

    render() {
        return (
            <div>
                <input type="text" name="name" value={this.state.counter}/>
                <button type="button" value={this.state.counter} onClick={this.handleClick}>Click</button>
            </div>

        )
    }
}



export default Counter;
